import React, { Component } from "react";
import { useForm } from "react-hook-form";

const Form = (props) => {
  const { register, handleSubmit } = useForm();
  const onSubit = (data) => {
    console.log(data);
  };

  return (
    <div>
      <h1>Form</h1>
      <form onSubmit={handleSubmit(onSubit)}>
        <h2>Step 1</h2>
        <label>
          First Name:
          <input
            name="firstName"
            ref={register}
            defaultValue={state.data.firstName}
          />
        </label>
        <label>
          Last Name:
          <input
            name="lastName"
            ref={register}
            defaultValue={state.data.lastName}
          />
        </label>
        <input type="submit" />
      </form>
    </div>
  );
};
export default Form;
