import { createStore, applyMiddleware } from "redux";
import { composeWithDevTools } from "redux-devtools-extension";
import { persistReducer } from "redux-persist";
import storage from "redux-persist/lib/storage";

const exampleInitialState = {
  lastUpdate: 0,
  light: false,
  count: 0,
  form: {
    firstName: "test",
    lastName: "",
  },
  exampleData: [],
  error: null,
};
export const actionTypes = {
  TICK: "TICK",
  Submit: "Submit",
  INCREMENT: "INCREMENT",
  DECREMENT: "DECREMENT",
  RESET: "RESET",
  LOAD_EXAMPLE_DATA: "LOAD_EXAMPLE_DATA",
  LOADING_DATA_FAILURE: "LOADING_DATA_FAILURE",
};

// REDUCERS
export const reducer = (state = exampleInitialState, action) => {
  switch (action.type) {
    case actionTypes.Submit:
      return Object.assign({}, state, {
        form: state.form,
      });
    case actionTypes.TICK:
      return Object.assign({}, state, {
        lastUpdate: action.ts,
        light: !!action.light,
      });
    case actionTypes.INCREMENT:
      return Object.assign({}, state, {
        count: state.count + 1,
      });
    case actionTypes.DECREMENT:
      return Object.assign({}, state, {
        count: state.count - 1,
      });
    case actionTypes.RESET:
      return Object.assign({}, state, {
        count: exampleInitialState.count,
      });
    case actionTypes.LOAD_EXAMPLE_DATA:
      return Object.assign({}, state, {
        exampleData: action.data,
      });
    case actionTypes.LOADING_DATA_FAILURE:
      return Object.assign({}, state, {
        error: true,
      });
    default:
      return state;
  }
};
export const SubmitForm = (data) => {
  return { type: actionTypes.Submit, data };
};

const persistConfig = {
  key: "primary",
  storage,
  whitelist: [""], // place to select which state you want to persist
};

const persistedReducer = persistReducer(persistConfig, reducer);

export function initializeStore(initialState = exampleInitialState) {
  return createStore(
    persistedReducer,
    initialState,
    composeWithDevTools(applyMiddleware())
  );
}
